	public static void sceduleBirthdayFile() {
		calendar = Calendar.getInstance();
		day.set(calendar.get(Calendar.YEAR), calendar.get(Calendar.MONTH), calendar.get(Calendar.DAY_OF_MONTH), 0, 0,
				0);
		timer = new Timer();
		timer.schedule(new TimerTask() {
			@Override
			public void run() {
				calendar = Calendar.getInstance();
				int m = calendar.get(Calendar.MINUTE);
				int s = calendar.get(Calendar.SECOND);
				int h = calendar.get(Calendar.HOUR_OF_DAY);
				if (h == 0 && m == 0 && s == 0) {
					if (SpammerV2AndBot.isBirthday) {
						String text = "אני הבוט של אור ני ני ני ואני אומר שנגמר ל" + SpammerV2AndBot.birthdayName
								+ " היום הולדת😭😭😭";
						SpammerV2AndBot.sendText(text);
					}
					cancel();
				} else if (((m == 30 && s == 0) || (m == 0 && s == 0)) && SpammerV2AndBot.isBirthday) {
					long current = ((calendar.getTimeInMillis() - day.getTimeInMillis()) + 3000) / 60000;
					sendTime(current + "");
				}
			}
		}, 0, 1000);
	}

	// this function sends the birthday voice message.
	public static void sendTime(String minutes) {
		try {
			Talker.makeFile(minutes);
		} catch (IOException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (URISyntaxException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (InterruptedException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (ExecutionException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (UnsupportedAudioFileException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} catch (LineUnavailableException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		}
		try {
			String osname = System.getProperty("os.name");
			File file = null;
			if (osname.startsWith("Linux")) {
				file = new File(MessageSearch.linuxBotSongsFolderPath + "Happy Birthday.wav");
			} else if (osname.startsWith("Windows")) {
				file = new File(MessageSearch.windowsBotSongsFolderPath + "Happy Birthday.wav");
			}
			SpammerV2AndBot.sendFile(file);
		} catch (Exception e) {
			e.printStackTrace();
			// TODO: handle exception
		}
	}

